conditionMonitoringQueryLanguage.query:
{
    t_0: fetch gce_instance::workload.googleapis.com/cassandra.client.request.count
    ; 
    t_1: fetch gce_instance::workload.googleapis.com/cassandra.client.request.error.count
}
| outer_join 1
| group_by [metric.status, metric.operation]
| value val(1) / if(has_value(val(0)), 1, val(0))
| window 1m
| condition val() > .1
