conditionPrometheusQueryLanguage.query:
(
  sum by (matched_url_path_rule) (
    rate({
      "loadbalancing.googleapis.com/https/request_count",
      monitored_resource="https_lb_rule",
      project_id=~"${metric_or_resource.label.project_id}",
      backend_name=~"${NETWORK_ENDPOINT_GROUPS_REGEX}",
      backend_type="NETWORK_ENDPOINT_GROUP",
      response_code_class="500"
    }[5m])
  )
  /
  sum by (matched_url_path_rule) (
    rate({
      "loadbalancing.googleapis.com/https/request_count",
      monitored_resource="https_lb_rule",
      project_id=~"${metric_or_resource.label.project_id}",
      backend_name=~"${NETWORK_ENDPOINT_GROUPS_REGEX}",
      backend_type="NETWORK_ENDPOINT_GROUP"
    }[5m])
  )
) > 0.05
