conditionPrometheusQueryLanguage.query:
(
  sum by (grpc_method) (
    rate({
      "grpc_server_handled_total",
      project_id=~"${metric_or_resource.label.project_id}",
      cluster=~"${metric_or_resource.label.cluster_name}",
      location=~"${metric_or_resource.label.location}",
      namespace=~"${metric_or_resource.label.namespace_name}",
      pod=~"${metric.label.metadata_system_top_level_controller_name}-[a-f0-9]{7,10}-[a-z0-9]{5}",
      grpc_code=~"(Internal|Unknown|Unavailable|Unimplemented)"
    }[5m])
  )
  /
  sum by (grpc_method) (
    rate({
      "grpc_server_handled_total",
      project_id=~"${metric_or_resource.label.project_id}",
      cluster=~"${metric_or_resource.label.cluster_name}",
      location=~"${metric_or_resource.label.location}",
      namespace=~"${metric_or_resource.label.namespace_name}",
      pod=~"${metric.label.metadata_system_top_level_controller_name}-[a-f0-9]{7,10}-[a-z0-9]{5}"
    }[5m])
  )
) > 0.05
