conditionPrometheusQueryLanguage.query:
(
  sum by (grpc_method) (
    rate({
      "grpc_server_handled_total",
      project_id=~"${PROJECT_ID}",
      cluster=~"${CLUSTER_NAME}",
      location=~"${CLUSTER_LOCATION}",
      namespace=~"${NAMESPACE}",
      pod=~"${WORKLOAD_NAME}-[a-f0-9]{7,10}-[a-z0-9]{5}",
      grpc_code=~"(Internal|Unknown|Unavailable|Unimplemented)"
    }[5m])
  )
  /
  sum by (grpc_method) (
    rate({
      "grpc_server_handled_total",
      project_id=~"${PROJECT_ID}",
      cluster=~"${CLUSTER_NAME}",
      location=~"${CLUSTER_LOCATION}",
      namespace=~"${NAMESPACE}",
      pod=~"${WORKLOAD_NAME}-[a-f0-9]{7,10}-[a-z0-9]{5}"
    }[5m])
  )
) > 0.05
