conditionPrometheusQueryLanguage.query:
(
  sum (
    rate({
      "istio_requests_total",
      project_id="${PROJECT_ID}",
      cluster="${CLUSTER_NAME}",
      location="${CLUSTER_LOCATION}",
      namespace="${NAMESPACE}",
      destination_workload=~"${WORKLOAD_NAME}",
      response_code=~"5[0-9][0-9]"
    }[5m])
  )
  /
  sum (
    rate({
      "istio_requests_total",
      project_id="${PROJECT_ID}",
      cluster="${CLUSTER_NAME}",
      location="${CLUSTER_LOCATION}",
      namespace="${NAMESPACE}",
      destination_workload=~"${WORKLOAD_NAME}",
    }[5m])
  )
) > 0.05
