conditionMonitoringQueryLanguage.query:
fetch gce_zone_network_health :: networking.googleapis.com/cloud_netslo/active_probing/probe_count
 | {
   filter metric.result = 'failure'
   | group_by [resource.region, metric.remote_region], 60s, .sum
 ;
   group_by [
     resource.region,
     metric.remote_region
   ], 60s, .sum
   | filter val() >= 1 '1'
 }
 | ratio | condition val() > 0.05
