conditionPrometheusQueryLanguage.query:
(
    sum by (region, remote_region) (
        rate({"networking.googleapis.com/cloud_netslo/active_probing/probe_count", monitored_resource="gce_zone_network_health", result="failure"}[60s])
    )
    /
    (
        sum by (region, remote_region) (
            rate({"networking.googleapis.com/cloud_netslo/active_probing/probe_count", monitored_resource="gce_zone_network_health"}[60s])
        )
    )
) > 0.05
