conditionPrometheusQueryLanguage.query:
(
  (sum by (region, remote_region) (
    {"networking.googleapis.com/cloud_netslo/active_probing/probe_count", monitored_resource="gce_zone_network_health", result="failure"}
  ))
  /
  clamp_min(
    sum by (region, remote_region)(
      {"networking.googleapis.com/cloud_netslo/active_probing/probe_count", monitored_resource="gce_zone_network_health"}
    ), 1
  ) 
) > 0.05
