conditionPrometheusQueryLanguage.query:
(
  sum by ('${GROUPING}') (
    sum_over_time(
        (
            {
                '${METRIC_USAGE_TYPE}',
                monitored_resource='${MONITORED_RESOURCE}',
                '${OUTER_FILTER}',
                '${QUOTA_USAGE_FILTER}',
                '${OPTIONAL_USAGE_QUERY}'
            }
            *
            # only the last 24 hours since midnight Los Angeles time (UTC-8)
            clamp_max(day_of_year(max(timestamp({'${METRIC_USAGE_TYPE}',monitored_resource='${MONITORED_RESOURCE}','${OUTER_FILTER}','${QUOTA_USAGE_FILTER}','${OPTIONAL_USAGE_QUERY}'} @ end()))) == day_of_year(max(timestamp({'${METRIC_USAGE_TYPE}',monitored_resource='${MONITORED_RESOURCE}','${OUTER_FILTER}','${QUOTA_USAGE_FILTER}','${OPTIONAL_USAGE_QUERY}'} @ end())) - (8*3600)), 1) 
        )[1d]
    )
  )
  /
  min by ('${GROUPING}') ({
    '${METRIC_LIMIT_TYPE}',
    monitored_resource='${MONITORED_RESOURCE}',
    '${OUTER_FILTER}',
    '${QUOTA_LIMIT_FILTER}'
  })
) > 0.8
