conditionMonitoringQueryLanguage.query:
def top_5_disk_filtered_by_metric filter_metric =
  fetch gce_instance
  | { t_memory:
        metric 'agent.googleapis.com/disk/percent_used'
    ; t_filter_metric: metric $filter_metric }
  | join
  | value val(0)
  | group_by [metadata.system.name, resource.project_id, resource.zone], 1m,
      .mean()
  | every 1m;

@top_5_disk_filtered_by_metric
  'workload.googleapis.com/mongodb.memory.usage'
| condition val() > 80'%'
