conditionPrometheusQueryLanguage.query:
(avg by (project_id, zone, instance_id) (
  avg_over_time(
    {"agent.googleapis.com/disk/percent_used", monitored_resource="gce_instance"}[1m]
  )
  and
  on(instance_id, project_id, zone)
    ({"workload.googleapis.com/mongodb.memory.usage", monitored_resource="gce_instance"})
  )
) > 80
