conditionMonitoringQueryLanguage.query:
{
     t_0: fetch gce_instance::workload.googleapis.com/oracle.tablespace.size
| filter (metric.state == 'used') ;
     t_1: fetch gce_instance::workload.googleapis.com/oracle.tablespace.size
| filter (metric.state == 'free')
}
| group_by [resource.instance_id, metric.tablespace_name]
| outer_join 0
| window 1m
| condition (val(0) + val(1)) * .85 < val(0)
