def top_5_memory_filtered_by_metric filter_metric =
  fetch cloudsql_database
  | { t_memory:
        metric 'cloudsql.googleapis.com/database/cpu/utilization'
    ; t_filter_metric: metric $filter_metric }
  | join
  | value val(0)
  | group_by [metadata.system.name, resource.project_id], 1m,
      .mean()
  | top 5
  | every 1m;

  @top_5_memory_filtered_by_metric 'cloudsql.googleapis.com/database/replication/state'

