    label_replace(
        sum by (location, cluster_name) (
            (
                rate(kubernetes_io:container_cpu_core_usage_time{monitored_resource="k8s_container"}[1m])
                or 
                rate(kubernetes_io:anthos_container_cpu_core_usage_time{monitored_resource="k8s_container"}[1m])
            )
        )
        /
