timeSeriesQuery.prometheusQuery:
100 *
label_replace(
    sum by (location, cluster_name) (
        rate(kubernetes_io:container_cpu_core_usage_time{monitored_resource="k8s_container"}[${__interval}])
        or 
        rate(kubernetes_io:anthos_container_cpu_core_usage_time{monitored_resource="k8s_container"}[${__interval}])
    )
    /
    sum by (location, cluster_name) (
        kubernetes_io:container_cpu_request_cores{monitored_resource="k8s_container"}
        or 
        kubernetes_io:anthos_container_cpu_request_cores{monitored_resource="k8s_container"}
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)

label_replace(
    sum by (location, cluster_name) (
        kubernetes_io:container_cpu_request_cores{monitored_resource="k8s_container"}
        or 
        kubernetes_io:anthos_container_cpu_request_cores{monitored_resource="k8s_container"}
    )
    -
    sum by (location, cluster_name) (
        rate(kubernetes_io:container_cpu_core_usage_time{monitored_resource="k8s_container"}[${__interval}])
        or 
        rate(kubernetes_io:anthos_container_cpu_core_usage_time{monitored_resource="k8s_container"}[${__interval}])
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)

100 *
label_replace(
    sum by (location, cluster_name) (
        rate(kubernetes_io:node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
        or 
        rate(kubernetes_io:anthos_node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
    )
    /
    sum by (location, cluster_name) (
        kubernetes_io:node_cpu_total_cores{monitored_resource="k8s_node"}
        or 
        kubernetes_io:anthos_node_cpu_total_cores{monitored_resource="k8s_node"}
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)

label_replace(
    (
        sum by (location, cluster_name) (
            kubernetes_io:node_cpu_total_cores{monitored_resource="k8s_node"}
        )
        -
        sum by (location, cluster_name) (
            rate(kubernetes_io:node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
        )
    )
    or
    (
        sum by (location, cluster_name) (
            kubernetes_io:anthos_node_cpu_total_cores{monitored_resource="k8s_node"}
        )
        -
        sum by (location, cluster_name) (
            rate(kubernetes_io:anthos_node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
        )
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)

100 *
label_replace(
    sum by (location, cluster_name) (
        (
            kubernetes_io:container_cpu_request_cores{monitored_resource="k8s_container"}
            or 
            kubernetes_io:anthos_container_cpu_request_cores{monitored_resource="k8s_container"}
        )
    )
    /
    sum by (location, cluster_name) (
        (
            kubernetes_io:node_cpu_allocatable_cores{monitored_resource="k8s_node"}
            or 
            kubernetes_io:anthos_node_cpu_allocatable_cores{monitored_resource="k8s_node"}
        )
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)

label_replace(
    sum by (location, cluster_name) (
        kubernetes_io:node_cpu_total_cores{monitored_resource="k8s_node"}
        or 
        kubernetes_io:anthos_node_cpu_total_cores{monitored_resource="k8s_node"}
    )
    -
    sum by (location, cluster_name) (
        rate(kubernetes_io:node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
        or 
        rate(kubernetes_io:anthos_node_cpu_core_usage_time{monitored_resource="k8s_node"}[${__interval}])
    ),
    "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
)
