    label_replace(
        sum by (location, cluster_name) (
            kubernetes_io:container_memory_used_bytes{monitored_resource="k8s_container"}
            or 
            kubernetes_io:anthos_container_memory_used_bytes{monitored_resource="k8s_container"}
        )
        /
        sum by (location, cluster_name) (
            kubernetes_io:container_memory_request_bytes{monitored_resource="k8s_container"}
            or 
            kubernetes_io:anthos_container_memory_request_bytes{monitored_resource="k8s_container"}
        ),
        "cluster_name", "$1", "cluster_name", "(?:.+/|^)(.+)"
    )
)

