timeSeriesQuery.prometheusQuery:
100 *
(
  sum by (namespace_name) (
    rate(kubernetes_io:container_cpu_core_usage_time{monitored_resource="k8s_container"}[1m])
    or
    rate(kubernetes_io:anthos_container_cpu_core_usage_time{monitored_resource="k8s_container"}[1m])
  )
  /
  (
    sum by (namespace_name) (
      kubernetes_io:container_cpu_request_cores{monitored_resource="k8s_container"}
      or
      kubernetes_io:anthos_container_cpu_request_cores{monitored_resource="k8s_container"}
    )
    and
    (
      sum by (namespace_name) (
        kubernetes_io:container_cpu_request_cores{monitored_resource="k8s_container"}
        or
        kubernetes_io:anthos_container_cpu_request_cores{monitored_resource="k8s_container"}
      ) > 0
    )
  )
)

100 *
(
  sum by (namespace_name) (
    kubernetes_io:container_memory_used_bytes{monitored_resource="k8s_container"}
    or
    kubernetes_io:anthos_container_memory_used_bytes{monitored_resource="k8s_container"}
  )
  /
  (
    sum by (namespace_name) (
      kubernetes_io:container_memory_request_bytes{monitored_resource="k8s_container"}
      or
      kubernetes_io:anthos_container_memory_request_bytes{monitored_resource="k8s_container"}
    )
    and
    (
      sum by (namespace_name) (
        kubernetes_io:container_memory_request_bytes{monitored_resource="k8s_container"}
        or
        kubernetes_io:anthos_container_memory_request_bytes{monitored_resource="k8s_container"}
      ) > 0
    )
  )
)
