timeSeriesQuery.prometheusQuery:
label_replace(
  (
    sum by (cluster_name, location, project_id) (
      increase(kubernetes_io:container_cpu_core_usage_time{
        monitored_resource="k8s_container",
        namespace_name!~"^(kube-system|istio-system|gatekeeper-system|gke-system)$"
      }[7d])
    )
    /
    (
      604800 *
      sum by (cluster_name, location, project_id) (
        avg_over_time(kubernetes_io:node_cpu_allocatable_cores{monitored_resource="k8s_node"}[7d])
      )
    )
  ) * 100,
  "cluster_status", "running", "", ""
)
and on (cluster_name, location, project_id)
(
  count by (cluster_name, location, project_id) (
    avg_over_time(kubernetes_io:container_uptime{
      monitored_resource="k8s_container",
      namespace_name="kube-system"
    }[7d])
  ) > 0
)

label_replace(
    count by (cluster_name, location, project_id) (
    kubernetes_io:container_uptime{monitored_resource="k8s_container", namespace_name!~"^istio-system|gatekeeper-system|gke-system$"}
    )
    -
    count by (cluster_name, location, project_id) (
    kubernetes_io:container_uptime{monitored_resource="k8s_container", namespace_name="kube-system"}
    ),
    "cluster_status", "running", "", ""
)
and on (cluster_name, location, project_id)
(
    count by (cluster_name, location, project_id) (
      kubernetes_io:container_uptime{monitored_resource="k8s_container", namespace_name="kube-system"}
    ) > 0
)
