timeSeriesQuery.timeSeriesQueryLanguage:
{ fetch k8s_container::kubernetes.io/container/cpu/request_cores
| filter
    resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp|gke-gmp)-system|gke-managed-filestorecsi|gke-mcs'
| ${project_id}
| ${location}
| ${cluster_name}
    | group_by [], .aggregate()
    ;
 fetch k8s_node::kubernetes.io/node/cpu/allocatable_cores
| ${project_id}
| ${location}
| ${cluster_name}
 | group_by [], .aggregate()
} 
| join
| value val(0) / val(1)
| scale '%'

